import { useState, useEffect } from "react";
import { db } from "./firebase";
import { ref, set, remove, onValue } from "firebase/database";

const brothers = [
  "Ïù¥ÌÉúÏÑ≠", "ÍπÄÏßÑÏö∞", "Ïù¥ÎèôÌôò", "Î¨∏ÏßÄÌõà", "Î∞ïÏßÄÏõÖ", "ÍπÄÏù∏Ïàò",
  "ÏµúÏû¨ÌòÅ", "ÎÇ®Ï§ÄÌòÅ", "Ï†ÑÎèÑÏòÅ", "ÍπÄÍ∂åÎπÑ", "ÍπÄÎëêÌòÑ", "Ï†ÑÌò∏ÏÑ±",
  "ÍπÄÎØºÌò∏", "Î∞ïÌö®Ïõê"
];

const sisters = [
  "Ï†ÑÌö®ÏßÑ", "Í≥†Îã§ÏòÅ", "ÎÇ®ÏÜåÏó∞", "ÍπÄÏú†ÎÇò", "Ï†ïÏó¨ÏßÑ", "ÍπÄÎØºÏÑú",
  "ÍπÄÏù¥Ïïà", "Î∞ïÏßÄÌòú", "Ïù¥ÌòÑÌôî", "Ïã¨Ïû¨Í≤Ω", "Ïú†ÏßÄÏó∞", "ÍπÄÏÜåÏùÄ",
  "ÏûÑÎèÑÌï¥", "Ïã¨Í∑úÎ¶¨", "Ïù¥ÏÜåÌù¨", "Ïã¨ÏòàÎ¶º"
];

function getToday() {
  return new Date().toISOString().split("T")[0];
}

export default function App() {
  const [attendance, setAttendance] = useState({});
  const [date, setDate] = useState(getToday());

  useEffect(() => {
    const attendanceRef = ref(db, `attendance/${date}`);
    onValue(attendanceRef, (snapshot) => {
      const data = snapshot.val() || {};
      setAttendance(data);
    });
  }, [date]);

  const toggle = (name) => {
    const attendanceRef = ref(db, `attendance/${date}/${name}`);
    if (attendance[name]) {
      remove(attendanceRef);
    } else {
      set(attendanceRef, true);
    }
  };

  const renderList = (title, names) => (
    <div className="flex-1 p-4">
      <h2 className="text-xl font-bold mb-4 text-center">{title}</h2>
      <div className="grid grid-cols-2 gap-2">
        {names.map((name) => (
          <div
            key={name}
            onClick={() => toggle(name)}
            className={
              "p-3 rounded-xl shadow-md text-center cursor-pointer text-base font-medium transition-all " +
              (attendance[name] ? "bg-green-400 text-white" : "bg-white")
            }
          >
            {name}
          </div>
        ))}
      </div>
    </div>
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-white to-sky-100 p-6">
      <h1 className="text-2xl font-bold text-center mb-4">üìã Ï∂úÏÑùÎ∂Ä</h1>
      <div className="flex justify-center mb-6">
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          className="border px-3 py-2 rounded-lg shadow-md"
        />
      </div>
      <div className="flex flex-col lg:flex-row gap-6">
        {renderList("ÌòïÏ†ú", brothers)}
        {renderList("ÏûêÎß§", sisters)}
      </div>
    </div>
  );
}
